import React from 'react';
import Avatar from "./Avatar";
import {useDispatch, useSelector} from "react-redux";
import {State, Stats, User} from "../utils/type";

const UserStats = () => {
    const {followers, following} = useSelector<State, Stats>(state => state.stats)
    const {name} = useSelector<State, User>(state => state.user)
    const dispatch = useDispatch()

    return (
        <div className='user-stats'>
            <div>
                <Avatar/>
                {name}
            </div>
            <div className='stats'>
                <div
                    onClick={() => changeStats('followers', true)}
                    onContextMenu={e => {
                        e.preventDefault();
                        changeStats('followers', false);
                    }}
                >Followers: {followers}</div>
                <div
                    onClick={() => changeStats('following', true)}
                    onContextMenu={e => {
                        e.preventDefault();
                        changeStats('following', false);
                    }}
                >Following: {following}</div>
            </div>
        </div>
    );
};

export default UserStats;
