import React from 'react';
import Avatar from "./Avatar";
import {useSelector} from "react-redux";
import {State, Stats, User} from "../utils/type";

const UserStats = () => {
    // const {user, stats, changeStats} = useContext(TwitterContext);
    const stats = useSelector<State, Stats>(state => state.stats)
    const user = useSelector<State, User>(state => state.user)


    return (
        <div className='user-stats'>
            <div>
                <Avatar/>
                {user.name}
            </div>
            <div className='stats'>
                <div
                    onClick={() => changeStats('followers', true)}
                    onContextMenu={e => {
                        e.preventDefault();
                        changeStats('followers', false);
                    }}
                >Followers: {stats.followers}</div>
                <div
                    onClick={() => changeStats('following', true)}
                    onContextMenu={e => {
                        e.preventDefault();
                        changeStats('following', false);
                    }}
                >Following: {stats.following}</div>
            </div>
        </div>
    );
};

export default UserStats;
