import React from "react";

export interface User {
    name: string,
    avatar: string
}

export interface Stats {
    followers: number,
    following: number
}

export interface TwitterContextValue {
    user: User,
    stats: Stats,
    changeAvatar: (url: string | null) => void,
    changeName: (name: string | null) => void,
    changeStats: (statsType: StatsType, isIncrement: boolean) => void
}

export type StatsType = 'followers' | 'following';

export const TwitterContext = React.createContext<TwitterContextValue>({
    user: {
        name: 'Monster',
        avatar: 'https://www.gravatar.com/avatar/0?d=monsterid'
    },
    stats: {
        followers: 0,
        following: 0
    },
    changeAvatar: (url: string | null) => {
    },
    changeName: (name: string | null) => {
    },
    changeStats: (statsType: StatsType, isIncrement: boolean) => {}
});
