import React, {useState} from "react";
import './App.css';
import Nav from "./components/Nav";
import Body from "./components/Body";
import {Stats, StatsType, TwitterContext, User} from "./utils/context";

function App() {
    const [user, setUser] = useState<User>({
        name: 'Monster',
        avatar: 'https://www.gravatar.com/avatar/0?d=monsterid'
    });
    const [stats, setStats] = useState<Stats>({
        followers: 0,
        following: 0
    })
    const changeAvatar = (url: string | null) => {
        setUser(user => ({...user, avatar: url || user.avatar}));
    }

    const changeName = (name: string | null) => {
        setUser(user => ({avatar: user.avatar, name: name || user.name}));
    }

    const changeStats = (statsType: StatsType, isIncrement: boolean) => {
        setStats(stats => {
            let res = stats[statsType] + (isIncrement ? 1 : -1);
            res = res < 0 ? 0 : res;
            return {...stats, [statsType]: res};
        });
    }

    return (
        <div className={'app'}>
            <TwitterContext.Provider value={{
                user, stats, changeAvatar, changeName, changeStats
            }}>
                <Nav/>
                <Body/>
            </TwitterContext.Provider>
        </div>
    );
}

export default App;
