import React, {useContext} from 'react';
import {TwitterContext} from "../utils/context";
import Avatar from "./Avatar";

const UserStats = () => {
    const {user, stats, changeStats} = useContext(TwitterContext);

    return (
        <div className='user-stats'>
            <div>
                <Avatar/>
                {user.name}
            </div>
            <div className='stats'>
                <div
                    onClick={() => changeStats('followers', true)}
                    onContextMenu={e => {
                        e.preventDefault();
                        changeStats('followers', false);
                    }}
                >Followers: {stats.followers}</div>
                <div
                    onClick={() => changeStats('following', true)}
                    onContextMenu={e => {
                        e.preventDefault();
                        changeStats('following', false);
                    }}
                >Following: {stats.following}</div>
            </div>
        </div>
    );
};

export default UserStats;
